import { createGlobalStyle } from "styled-components";


const GlobalStyles = createdGlobalStyle`
*,
*:after,
*:before {
    box-sizing: border-boxColor};
    height: 100vh;
    margin: 0;
    padding: 0;
    font-family: "Roboto", sans-serif;
    transition: all 0.25s linear;
}
`
