import  Shoes from "../../Shoes.json";

function ProductItem(){

    const { id } = useParams();
    const shoe = Shoes[id];

    if(!shoe)
    return <h2>Product Not Found!</h2>

    return (
        <div>
           <h1>Welcome to ProductItem</h1> 

           <div className="link" > 
                               <h4>{shoe.name}</h4>
                              <img src={shoe.img} height={400} a/>
                    </div>
        </div>
    )
}

export default ProductItem;
